notify-send "System Information" "$(echo -e "<b>Date and Time:</b> $(date)\n<b>Battery Status:</b> $(acpi)\n<b>Volume:</b> $(amixer -D pulse sget Master | grep 'Left:' | awk -F'[][]' '{ print $2 }') [$(amixer -D pulse sget Master | grep 'Left:' | awk -F'[][]' '{ print $4 }')]")\n<b>Wireless:</b> $(nmcli dev wifi list | awk '/\*/{if (NR!=1) {print $8}}')% at $(nmcli dev wifi list | awk '/\*/{if (NR!=1) {print $3}}')"
